import React from "react";
import axios from "axios";
import {
  API_URL,
  PRO_PREFIX,
  BACKLOG_API_KEY
} from "utilities/constants.js";

export default class App extends React.Component {
  constructor(props) {
    super(props);
    const date = new Date();
    this.state = {
      hour: this.checkTime(date.getHours()),
      minute: this.checkTime(date.getMinutes()),
      second: this.checkTime(date.getSeconds()),
      crt_minute: ""
    };
    this.autoUpdate = this.autoUpdate.bind(this);
    this.checkTime = this.checkTime.bind(this);
  }

  checkTime(elem) {
    if (elem < 10) {
      elem = "0" + elem;
    }
    return elem;
  }

  autoUpdate() {
    const date = new Date();
    this.setState({
      hour: this.checkTime(date.getHours()),
      minute: this.checkTime(date.getMinutes()),
      second: this.checkTime(date.getSeconds())
    });
    let crt_minute = this.state.crt_minute;
    let minute = this.state.minute;
    if (minute % 5 == 0 && minute != crt_minute) {
      this.setState({ crt_minute: minute });
      this.getDataLupack();
    }
  }

  componentDidMount() {
    window.setInterval(this.autoUpdate, 1000);
  }

  getDataLupack() {
    const url = API_URL + "fdoc_t_ticket";
    axios.get(url).then(response => {
      console.log(response.data);
    });
  }

  render() {
    const state = this.state;
    const time = state.hour + ":" + state.minute + ":" + state.second;
    return (
      <div className="App">
        <h1>{time}</h1>
      </div>
    );
  }
}
